</div><?php } else {	echo '<script>window.location.href = "index.php";</script>';} ?><script>function openSearch(evt, searchName) {  var i, x, tablinks;  x = document.getElementsByClassName("search-select");  for (i = 0; i < x.length; i++) {    x[i].style.display = "none";  }  tablinks = document.getElementsByClassName("tablink");  for (i = 0; i < x.length; i++) {    tablinks[i].className = tablinks[i].className.replace(" search-btn-selected", "");  }  document.getElementById(searchName).style.display = "block";  evt.currentTarget.className += " search-btn-selected";}</script>