<?php	if (isset($_SESSION['user-role'])) {		if (($_SESSION['user-role'] == 'Admin') || ($_SESSION['user-role'] == 'Pharmacy manager') || ($_SESSION['user-role'] == 'Dispensary user')){	$deleteReq = '';		$errors = array('cd-order-delete'=>'','reason'=>'');		// Delete CD req on confirmation	if(isset($_POST['delete-cd-order-submit'])){				$deleteReq = $_POST['delete-req-confirm'];		$firstNameConfirm = $_POST['first-name-confirm'];		$lastNameConfirm = $_POST['last-name-confirm'];		$extConfirm = $_POST['ext-confirm'];		$positionConfirm = $_POST['position-confirm'];		$wardConfirm = $_POST['ward-confirm'];		$drugNameConfirm = $_POST['drug-name-confirm'];		$quantityConfirm = $_POST['quantity-confirm'];		$orderedOnConfirm = $_POST['ordered-on-confirm'];		$deletedBy = $_SESSION['first-name'] . ' ' . $_SESSION{'surname'};		$deleteReason = $_POST['reason-confirm'];				$sql = "INSERT INTO cdReqNumberDeleted(reqNumberID, firstName, lastName, ext, position, ward, drugName, quantity, orderedOn, deletedBy, deletedReason) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";		$params = array($deleteReq, $firstNameConfirm, $lastNameConfirm, $extConfirm, $positionConfirm, $wardConfirm, $drugNameConfirm, $quantityConfirm, $orderedOnConfirm, $deletedBy, $deleteReason);		$stmt = sqlsrv_query($conn,$sql,$params);				// CHECK TO SEE IF ROW HAS BEEN SUCCESSFULLY ENTER INTO cdreqnumberdeleted, IF SO THEN DELETE FROM cdReqNumber				if(!$stmt){				echo 'insert error: ' . sqlsrv_errors($conn);				if($stmt === false ) {					if( ($errors = sqlsrv_errors() ) != null) {						foreach( $errors as $error ) {							echo "SQLSTATE: ".$error[ 'SQLSTATE']."<br />";							echo "code: ".$error[ 'code']."<br />";							echo "message: ".$error[ 'message']."<br />";						}					}				}			}	else {								$deleteCDOrderTable = "DELETE FROM cdReqNumber WHERE reqNumberID = '$deleteReq'";				$reqDeleted = '<b>' . $deleteReq . '</b>&nbsp;has been deleted.';								//include('cd-order-delete-mailer'); 									if (!sqlsrv_query($conn, $deleteCDOrderTable)){					echo 'delete error: ' . sqlsrv_errors($conn);				}				}			}	$result = $emptyWard = $resultRows = '';	// Database query	$sql = "SELECT * FROM cdreqnumber ORDER BY orderedOn";				//Get query	$result = sqlsrv_query($conn, $sql);	$resultRows = sqlsrv_has_rows($result);				if( $result === false) {						die( print_r( sqlsrv_errors(), true) );					}?><?php	if (isset($deleteCDOrderTable)) {		echo '<div class="success-ward-wrapper">';		echo '<div class="success-ward-container">';		echo $reqDeleted; 		echo '</div>';		echo '</div>';	}?>	<div class="list-table-wrapper"><h1>New CD Orders</h1>	<?php if ($resultRows === false) { ?>		<div class="no-orders">		No new CD orders	</div><?php } else { ?>		<table>			<tr>			<th>Req No.</th>			<th>Name</th>			<th>Ward</th>			<th>Drug</th>			<th>Quantity</th>			<th>Ordered</th>						<th>Delete</th>	    </tr>		<?php while ($row = sqlsrv_fetch_array($result, SQLSRV_FETCH_ASSOC)) { ?>						<tr>		                <td><?php echo $row['reqNumberID'] ?></td>    				<td><?php echo $row['firstName'] ?> <?php echo $row['lastName'] ?></td>    				<td><?php echo $row['ward'] ?></td>    				<td><?php echo $row['drugName'] ?></td>    				<td><?php echo $row['quantity'] ?></td>    				<td><?php echo $row['orderedOn']->format('d-M-Y'); ?></td>										<td><button class="cd-btn-delete" onclick="document.getElementById('row<?php echo $row['reqNumberID']; ?>').style.display='block'"><!--<i class="fas fa-times-circle" style="color:red"></i>-->X</button></td>					</tr>				<!-- When action button pressed this will display -->				<div class="delete-cd-wrapper" id="row<?php echo $row['reqNumberID']; ?>">					<div class="delete-cd-close">						<i class="fas fa-times" onclick="document.getElementById('row<?php echo $row['reqNumberID']; ?>').style.display='none'"></i>					</div>					<form method="POST" action="">						<div class="cd-delete-modal">													<div class="cd-delete-modal-left">								Requisition number:<br>								<?php echo $row['reqNumberID'] ?>								<input type="hidden" name="delete-req-confirm" value="<?php echo $row['reqNumberID'] ?>"><br><br>								Ordered by:<br>								<?php echo $row['firstName'] ?> <?php echo $row['lastName'] ?>								<input type="hidden" name="first-name-confirm" value="<?php echo $row['firstName'] ?>">								<input type="hidden" name="last-name-confirm" value="<?php echo $row['lastName'] ?>"><br><br>								Ward:<br>								<?php echo $row['ward'] ?>								<input type="hidden" name="ward-confirm" value="<?php echo $row['ward'] ?>"><br><br>								Drug:<br>								<?php echo $row['drugName'] ?>								<input type="hidden" name="drug-name-confirm" value="<?php echo $row['drugName'] ?>"><br><br>								Quantity:<br>								<?php echo $row['quantity'] ?>								<input type="hidden" name="quantity-confirm" value="<?php echo $row['quantity'] ?>"><br><br>								Date ordered:<br>								<?php echo $row['orderedOn']->format('d-M-Y'); ?>								<input type="hidden" name="ordered-on-confirm" value="<?php echo $row['orderedOn']->format('d-M-Y'); ?>">																<input type="hidden" name="ext-confirm" value="<?php echo $row['ext'] ?>">																<input type="hidden" name="position-confirm" value="<?php echo $row['position'] ?>">														</div>													<div>								Reason for deletion<br />								<select name="reason-confirm">									<option disabled selected value> -- Select an option -- </option>									<option>Duplicated</option>									<option>Restricted use</option>									<option>Unavailable</option>								</select>								<div class="error-message"><?php echo $errors['reason'] ?></div>								<br /><br />																<button type="submit" name="delete-cd-order-submit"><i class="fas fa-minus-circle"></i> DELETE</button>							</div>												</div>									</form>				</div>											<?php } ?>								</table></div><div class="process-orders-container">	<form target="_blank" method="POST" action="config/process-cd-orders.php" >		<button type="submit" name="process-cd-orders-submit">Process orders</button>	</form>	<a href="processed-cd-orders.php"><button style="margin-top:10px">View processed orders</button></a></div>		<?php } } 				    } else {				echo '<script>window.location.href = "index.php";</script>';				}?>