<?php 	$error = '';	if (isset($_POST['dateSubmit'])) {		if (empty($_POST['dateSearch'])) {			$error = 'Please select a date.';		} else {			$dateSearch = $_POST['dateSearch'];			//$sql = "SELECT * FROM stockOrderTable WHERE orderedOn LIKE '$dateSearch%' ORDER BY orderedOn DESC";			$sql = "SELECT * FROM stockOrderTable WHERE  CAST(orderedOn AS DATE) = '$dateSearch' ORDER BY orderedOn DESC";			$stmtDate = sqlsrv_query($conn, $sql);			$rowCountDate = sqlsrv_has_rows($stmtDate);		}	}?><div class="list-table-wrapper">			<h1>Print Stock Orders</h1>				<div class="audit-search-container">					<div class="search-select">							Please select a date<br /><br />								<form method="post" action="">									<input type="date" name="dateSearch"><span> <?php echo $error ?></span>					<br /><br />					<button type="submit" name="dateSubmit">Search</button>								</form>						</div>				</div>


		<table>

    					 	<?php  if (isset($dateSearch)) {			    			if ($rowCountDate === true) {				echo "<tr>			    	<th>Ward</th>			    	<th>Name</th>			    	<th>Ext</th>			    	<th>Drug</th>			    	<th>Quantity</th>			    	<th>Ordered</th>	    		</tr>";    			while ($row = sqlsrv_fetch_array($stmtDate)) { ?>    			<tr>    				<td><?php echo $row['4'] ?></td>    				<td><?php echo $row['1'] ?> <?php echo $row['2'] ?></td>    				<td><?php echo $row['3'] ?></td>    				<td><?php echo $row['5'] . ' ' . $row['6'] . ' ' . $row['7'] ?></td>    				<td><?php echo $row['8'] ?></td>    				<td><?php echo $row['9'] ?></td>										<td><button onclick="window.print()">Print</button></td>    			</tr> 								<?php } } else {							echo "<div class='no-orders'>";							echo "No orders for this date.";							echo "</div>";				} } else {										if (!isset($_POST['reqNumSubmit'])) {							echo "<div class='select-ward'>";							echo "Please search a field to view deleted controlled drug requisitions";							echo "</div>";						} } ?>
		</table>
</div>